import React, { useState, useEffect } from 'react';
import './App.css';

const SYMBOL_EMOJI = {
  'A': '1',
  'B': 'â­',
  'C': 'ðŸ’Ž',
  'D': 'â—‡',
};

function App() {
  const [balance, setBalance] = useState(457.00);
  const [betPerLine, setBetPerLine] = useState(5.40);
  const [lastWin, setLastWin] = useState(0.00);
  const [grid, setGrid] = useState(Array(3).fill(Array(3).fill('â—‡')));
  const [isSpinning, setIsSpinning] = useState(false);

  useEffect(() => {
    fetchBalance();
  }, []);

  const fetchBalance = async () => {
    try {
      const response = await fetch('http://localhost:8080/api/balance');
      const data = await response.json();
      setBalance(data.balance);
    } catch (error) {
      console.error('Error fetching balance:', error);
    }
  };

  const handleSpin = async () => {
    if (isSpinning) return;
    setIsSpinning(true);
    setLastWin(0);

    try {
      const response = await fetch('http://localhost:8080/api/spin', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ amount: Math.floor(betPerLine) }),
      });

      if (!response.ok) {
        throw new Error('Spin failed');
      }

      const data = await response.json();
      setGrid(data.grid.map(row => row.map(symbol => SYMBOL_EMOJI[symbol])));
      setBalance(data.balance);
      setLastWin(data.winnings);
      setIsSpinning(false);

    } catch (error) {
      setIsSpinning(false);
      alert('Error occurred while spinning');
    }
  };

  return (
    <div className="App">
      <div className="slot-machine">
        <div className="title-bar">GOLDEN VEGAS SLOTS</div>

        <div className="slot-grid">
          {grid.map((row, i) => (
            row.map((symbol, j) => (
              <div 
                key={`${i}-${j}`} 
                className={`symbol ${isSpinning ? 'spinning' : ''}`}
              >
                {symbol}
              </div>
            ))
          ))}
        </div>

        <div className="controls">
          <div className="bet-controls">
            <div className="control-label">LINES</div>
            <div className="bet-slider">
              <span>BET PER LINE</span>
              <span>${betPerLine.toFixed(2)}</span>
            </div>
          </div>

          <button 
            className="spin-button"
            onClick={handleSpin}
            disabled={isSpinning}
          >
            SPIN
          </button>

          <div className="game-stats">
            <div className="stat">
              <span>LAST WIN</span>
              <span>${lastWin.toFixed(2)}</span>
            </div>
            <div className="stat">
              <span>BALANCE</span>
              <span>${balance.toFixed(2)}</span>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
