import React, { useState, useEffect } from 'react';
import './App.css';

const SYMBOL_EMOJI = {
  'A': '1',
  'B': 'â­',
  'C': 'ðŸ’Ž',
  'D': 'â—‡',
};

const MIN_BET = 0.50;
const MAX_BET = 100.00;
const BET_INCREMENT = 0.50;

function App() {
  const [balance, setBalance] = useState(2250.00);
  const [betPerLine, setBetPerLine] = useState(2.90);
  const [totalBet, setTotalBet] = useState(7.50);
  const [multiplier, setMultiplier] = useState(37.70);
  const [grid, setGrid] = useState(Array(3).fill(Array(3).fill('7')));
  const [isSpinning, setIsSpinning] = useState(false);
  const [paylines] = useState([1, 2, 3]);
  const [lastWin, setLastWin] = useState(0);

  useEffect(() => {
    fetchBalance();
  }, []);

  useEffect(() => {
    // Update total bet when bet per line changes
    const newTotalBet = betPerLine * paylines.length;
    setTotalBet(Number(newTotalBet.toFixed(2)));
  }, [betPerLine, paylines.length]);

  const fetchBalance = async () => {
    try {
      const response = await fetch('http://localhost:8080/api/balance');
      const data = await response.json();
      setBalance(data.balance);
    } catch (error) {
      console.error('Error fetching balance:', error);
    }
  };

  const handleBetChange = (increment) => {
    const newBet = Number((betPerLine + increment).toFixed(2));
    if (newBet >= MIN_BET && newBet <= MAX_BET) {
      setBetPerLine(newBet);
    }
  };

  const handleSpin = async () => {
    if (isSpinning || totalBet > balance) return;
    setIsSpinning(true);
    setLastWin(0);

    // Deduct bet amount immediately
    setBalance(prev => Number((prev - totalBet).toFixed(2)));

    // Spinning animation
    const spinDuration = 2000;
    const interval = setInterval(() => {
      setGrid(prevGrid => 
        prevGrid.map(row => 
          row.map(() => {
            const symbols = Object.values(SYMBOL_EMOJI);
            return symbols[Math.floor(Math.random() * symbols.length)];
          })
        )
      );
    }, 100);

    try {
      const response = await fetch('http://localhost:8080/api/spin', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ amount: Math.floor(totalBet) }),
      });

      if (!response.ok) {
        throw new Error('Spin failed');
      }

      const data = await response.json();

      // Stop animation and update state
      setTimeout(() => {
        clearInterval(interval);
        setGrid(data.grid.map(row => row.map(symbol => SYMBOL_EMOJI[symbol])));
        setBalance(data.balance);
        if (data.winnings > 0) {
          setLastWin(data.winnings);
          setMultiplier(Number((data.winnings / totalBet).toFixed(2)));
        }
        setIsSpinning(false);
      }, spinDuration);

    } catch (error) {
      clearInterval(interval);
      setIsSpinning(false);
      alert('Error occurred while spinning');
    }
  };

  return (
    <div className="App">
      <div className="slot-machine">
        <div className="title-bar">GOLDEN VEGAS SLOTS</div>

        <div className="slot-grid">
          {[...Array(3)].map((_, reelIndex) => (
            <div key={reelIndex} className="reel">
              {grid.map((row, symbolIndex) => (
                <div 
                  key={symbolIndex}
                  className={`symbol ${isSpinning ? 'spinning' : ''}`}
                >
                  {row[reelIndex]}
                </div>
              ))}
            </div>
          ))}
        </div>

        <div className="bottom-controls">
          <div className="bet-section">
            <div className="control-label">LINES</div>
            <input 
              type="range" 
              min="1" 
              max="3" 
              value={paylines.length}
              className="line-slider"
              disabled={isSpinning}
            />
            <div className="bet-amount">
              <span>BET PER LINE</span>
              <div className="bet-value">{betPerLine.toFixed(2)}</div>
            </div>
          </div>

          <button 
            className="spin-button"
            onClick={handleSpin}
            disabled={isSpinning || totalBet > balance}
          >
            SPIN
          </button>

          <div className="stats">
            <div className="stat-item">
              <span>LAST WIN</span>
              <div className="value">${lastWin.toFixed(2)}</div>
            </div>
            <div className="stat-item">
              <span>BALANCE</span>
              <div className="value">${balance.toFixed(2)}</div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
