import React, { useState, useEffect } from 'react';
import './App.css';

const SYMBOL_EMOJI = {
  'A': 'üé∞',
  'B': 'üçí',
  'C': 'üîî',
  'D': 'üìä',
};

function App() {
  const [balance, setBalance] = useState(0);
  const [bet, setBet] = useState('');
  const [grid, setGrid] = useState(Array(3).fill(Array(3).fill('?')));
  const [isSpinning, setIsSpinning] = useState(false);
  const [message, setMessage] = useState('');

  useEffect(() => {
    fetchBalance();
  }, []);

  const fetchBalance = async () => {
    try {
      const response = await fetch('http://localhost:8080/api/balance');
      const data = await response.json();
      setBalance(data.balance);
    } catch (error) {
      console.error('Error fetching balance:', error);
    }
  };

  const handleSpin = async () => {
    if (isSpinning || !bet) return;

    setIsSpinning(true);
    setMessage('');

    // Animate spinning
    const animationInterval = setInterval(() => {
      setGrid(prevGrid => 
        prevGrid.map(row => 
          row.map(() => {
            const symbols = ['üé∞', 'üçí', 'üîî', 'üìä'];
            return symbols[Math.floor(Math.random() * symbols.length)];
          })
        )
      );
    }, 100);

    try {
      const response = await fetch('http://localhost:8080/api/spin', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ amount: parseInt(bet) }),
      });

      if (!response.ok) {
        throw new Error('Spin failed');
      }

      const data = await response.json();

      // Stop animation
      setTimeout(() => {
        clearInterval(animationInterval);
        setGrid(data.grid.map(row => row.map(symbol => SYMBOL_EMOJI[symbol])));
        setBalance(data.balance);
        setMessage(data.message);
        setIsSpinning(false);
      }, 1000);

    } catch (error) {
      clearInterval(animationInterval);
      setMessage('Error occurred while spinning');
      setIsSpinning(false);
    }
  };

  return (
    <div className="App">
      <h1>Slot Machine</h1>
      <div className="balance">Balance: ${balance}</div>

      <div className="slot-grid">
        {grid.map((row, i) => (
          <div key={i} className="row">
            {row.map((symbol, j) => (
              <div key={j} className="cell">
                {symbol}
              </div>
            ))}
          </div>
        ))}
      </div>

      <div className="controls">
        <input
          type="number"
          value={bet}
          onChange={(e) => setBet(e.target.value)}
          placeholder="Enter bet amount"
          disabled={isSpinning}
        />
        <button 
          onClick={handleSpin} 
          disabled={isSpinning || !bet || parseInt(bet) > balance}
        >
          {isSpinning ? 'Spinning...' : 'SPIN!'}
        </button>
      </div>

      {message && <div className="message">{message}</div>}

      <div className="legend">
        <h3>Symbol Values</h3>
        <p>üé∞ Lucky 7 (10x)</p>
        <p>üçí Cherry (5x)</p>
        <p>üîî Bell (3x)</p>
        <p>üìä Bar (2x)</p>
      </div>
    </div>
  );
}

export default App;
