import React, { useState, useEffect } from 'react';
import './App.css';

const SYMBOL_EMOJI = {
  'A': '7Ô∏è‚É£',
  'B': 'üé∞',
  'C': '‚≠ê',
  'D': 'üíé',
};

function App() {
  const [balance, setBalance] = useState(0);
  const [bet, setBet] = useState('');
  const [grid, setGrid] = useState(Array(3).fill(Array(3).fill('‚ùì')));
  const [isSpinning, setIsSpinning] = useState(false);
  const [message, setMessage] = useState('');
  const [winnings, setWinnings] = useState(0);
  const [lastBet, setLastBet] = useState(0);

  useEffect(() => {
    fetchBalance();
  }, []);

  const fetchBalance = async () => {
    try {
      const response = await fetch('http://localhost:8080/api/balance');
      const data = await response.json();
      setBalance(data.balance);
    } catch (error) {
      console.error('Error fetching balance:', error);
    }
  };

  const handleSpin = async () => {
    if (isSpinning || !bet) return;

    setIsSpinning(true);
    setMessage('');
    setLastBet(parseInt(bet));
    setWinnings(0);

    // Animate spinning
    const animationInterval = setInterval(() => {
      setGrid(prevGrid => 
        prevGrid.map(row => 
          row.map(() => {
            const symbols = Object.values(SYMBOL_EMOJI);
            return symbols[Math.floor(Math.random() * symbols.length)];
          })
        )
      );
    }, 100);

    try {
      const response = await fetch('http://localhost:8080/api/spin', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ amount: parseInt(bet) }),
      });

      if (!response.ok) {
        throw new Error('Spin failed');
      }

      const data = await response.json();

      // Stop animation
      setTimeout(() => {
        clearInterval(animationInterval);
        setGrid(data.grid.map(row => row.map(symbol => SYMBOL_EMOJI[symbol])));
        setBalance(data.balance);
        setMessage(data.message);
        setWinnings(data.winnings);
        setIsSpinning(false);
      }, 1000);

    } catch (error) {
      clearInterval(animationInterval);
      setMessage('Error occurred while spinning');
      setIsSpinning(false);
    }
  };

  return (
    <div className="App">
      <div className="balance-display">Balance: ${balance}</div>

      <div className="slot-machine">
        <div className="bet-display">Current Bet: ${lastBet}</div>
        <div className="slot-grid">
          {grid.map((row, i) => (
            row.map((symbol, j) => (
              <div key={`${i}-${j}`} className={`cell ${isSpinning ? 'spinning' : ''}`}>
                {symbol}
              </div>
            ))
          ))}
        </div>

        {winnings > 0 && (
          <div className="winning-display">Won: ${winnings}</div>
        )}

        <div className="controls">
          <input
            type="number"
            className="bet-input"
            value={bet}
            onChange={(e) => setBet(e.target.value)}
            placeholder="Enter bet"
            disabled={isSpinning}
          />
          <button 
            className="spin-button"
            onClick={handleSpin} 
            disabled={isSpinning || !bet || parseInt(bet) > balance}
          >
            {isSpinning ? 'SPINNING...' : 'SPIN!'}
          </button>
        </div>
      </div>

      {message && <div className="message">{message}</div>}

      <div className="symbol-values">
        <h3>Symbol Values</h3>
        <p>7Ô∏è‚É£ Lucky 7 (10x)</p>
        <p>üé∞ Machine (5x)</p>
        <p>‚≠ê Star (3x)</p>
        <p>üíé Diamond (2x)</p>
      </div>
    </div>
  );
}

export default App;
